import { RequestInit } from 'node-fetch';

declare type Video = {
    id: string;
    title: string;
    name: string;
};
declare type Playlist = {
    title: string;
    playlist: Video[];
};

/**
 * Try and scrape the playlist for a given ID
 *
 * @param playlistId - ID of the playlist we wish to scrape
 */
declare function scrapePlaylist(playlistId: string, fetchOptions?: RequestInit): Promise<Playlist>;

export { Playlist, Video, scrapePlaylist };
